    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        // Datos
        int n = Integer.parseInt(txtN.getText());
        // Proceso
        MateController controller = new MateController();
        MateDto bean = new MateDto(n);
        bean = controller.procesar(bean);
        // Reporte
        txtFuente.setText(Arrays.toString(bean.getFuente()));
        txtPares.setText(Arrays.toString(bean.getPares()));
        txtSumaPares.setText("" + bean.getSumapares());
        txtImpares.setText(Arrays.toString(bean.getImpares()));
        txtSumaImpares.setText("" + bean.getSumaImpares());
    }//GEN-LAST:event_btnProcesarActionPerformed

